mapper.update:
update ${prefix}ACT_ID_GROUP set
  REV_ = #{revisionNext ,jdbcType=INTEGER},
  NAME_ = #{name ,jdbcType=VARCHAR},
  TYPE_ = #{type ,jdbcType=VARCHAR}
where ID_ = #{id}
   and REV_ = #{revision}

mapper.select:
	${limitBefore}
  select RES.id as ID_,1 as REV_ ,RES.role_name as NAME_ ,NULL as TYPE_
  ${limitBetween}

  ${orderBy}
  ${limitAfter}

mapper.sql:
from ${prefix}sys_role RES

  inner join ${prefix}sys_role_user M on RES.id = M.role_id
  inner join ${prefix}sys_user U on M.user_id = U.id



    RES.id = #{id}


    and RES.role_name = #{name}


    and RES.role_name like #{nameLike}${wildcardEscapeClause}


    and U.id = #{userId}
